{
    "@type": "https://w3c-ccg.github.io/vc-json-schemas/schema/1.0/schema.json",
    "version": "0.3",
    "id": "idhp-vaccination",
    "name": "Proof of Vaccination",
    "schema": {
        "$schema": "http://json-schema.org/draft-07/schema#",
        "properties": {
            "booster": {
                "type": "boolean"
            },
            "countryOfVaccination": {
                "system": "https://www.iso.org/iso-3166-country-codes.html",
                "type": "string"
            },
            "dateOfVaccination": {
                "displayValue": {
                    "de": "Impfdatum",
                    "en": "Vaccination Date"
                },
                "format": "date",
                "type": "string"
            },
            "disease": {
                "displayValue": {
                    "de": "Zielkrankheit",
                    "en": "Target Disease"
                },
                "type": "string"
            },
            "display": {
                "description": "Card background color.",
                "pattern": "#\\w{6}",
                "type": "string",
                "visible": false
            },
            "doseNumber": {
                "maxLength": 2,
                "minLength": 1,
                "type": "integer"
            },
            "dosesPerCycle": {
                "maxLength": 2,
                "minLength": 1,
                "type": "integer"
            },
            "marketingAuthorizationHolder": {
                "displayValue": {
                    "de": "Hersteller",
                    "en": "Manufacturer"
                },
                "type": "string"
            },
            "medicinalProductCode": {
                "displayValue": {
                    "de": "Impfstoff-Code",
                    "en": "Vaccine Code"
                },
                "type": "string"
            },
            "medicinalProductName": {
                "displayValue": {
                    "de": "Impfstoff",
                    "en": "Vaccine"
                },
                "type": "string"
            },
            "recipient": {
                "properties": {
                    "birthDate": {
                        "displayValue": {
                            "de": "Geburtsdatum",
                            "en": "Date of Birth"
                        },
                        "format": "date",
                        "pattern": "([0-9]([0-9]([0-9][1-9]|[1-9]0)|[1-9]00)|[1-9]000)(-(0[1-9]|1[0-2])(-(0[1-9]|[1-2][0-9]|3[0-1]))?)?",
                        "type": "string",
                        "visible": true
                    },
                    "familyName": {
                        "displayValue": {
                            "de": "Nachname",
                            "en": "Surname"
                        },
                        "maxLength": 100,
                        "minLength": 1,
                        "type": "string",
                        "visible": true
                    },
                    "givenName": {
                        "displayValue": {
                            "de": "Vorname",
                            "en": "First Name"
                        },
                        "maxLength": 100,
                        "minLength": 1,
                        "type": "string",
                        "visible": true
                    },
                    "identity": {
                        "properties": {
                            "DL": {
                                "displayValue": {
                                    "de": "",
                                    "en": "Driver's license number"
                                },
                                "maxLength": 50,
                                "minLength": 1,
                                "type": "string",
                                "visible": true
                            },
                            "MR": {
                                "displayValue": {
                                    "de": "",
                                    "en": "Medical record number"
                                },
                                "maxLength": 50,
                                "minLength": 1,
                                "type": "string",
                                "visible": true
                            },
                            "OTH": {
                                "displayValue": {
                                    "de": "",
                                    "en": "Other ID"
                                },
                                "maxLength": 50,
                                "minLength": 1,
                                "type": "string",
                                "visible": true
                            },
                            "PPN": {
                                "displayValue": {
                                    "de": "",
                                    "en": "Passport number"
                                },
                                "maxLength": 50,
                                "minLength": 1,
                                "type": "string",
                                "visible": true
                            }
                        },
                        "type": "object"
                    },
                    "middleName": {
                        "maxLength": 100,
                        "minLength": 1,
                        "type": "string",
                        "visible": true
                    }
                },
                "required": [
                    "givenName",
                    "familyName",
                    "birthDate"
                ],
                "type": "object"
            },
            "stateOfVaccination": {
                "system": "https://pe.usps.com/text/pub28/28apb.htm",
                "type": "string"
            },
            "type": {
                "maxLength": 100,
                "minLength": 1,
                "type": "string",
                "visible": false
            },
            "vaccinationHistory": {
                "items": {
                    "properties": {
                        "dateOfVaccination": {
                            "displayValue": {
                                "de": "Impfdatum",
                                "en": "Vaccination Date"
                            },
                            "format": "date",
                            "type": "string"
                        },
                        "medicinalProductCode": {
                            "displayValue": {
                                "de": "Impfstoff-Code",
                                "en": "Vaccine Code"
                            },
                            "type": "string"
                        }
                    },
                    "required": [
                        "medicinalProductCode",
                        "dateOfVaccination"
                    ],
                    "type": "object"
                },
                "minItems": 1,
                "type": "array"
            }
        },
        "required": [
            "type",
            "recipient",
            "medicinalProductCode",
            "marketingAuthorizationHolder",
            "doseNumber",
            "dosesPerCycle",
            "dateOfVaccination",
            "countryOfVaccination",
            "disease"
        ],
        "type": "object"
    },
    "authorName": "Digital Health Pass Issuer"
}

{
    "id": "verifierlogin",
    "name": "Verifier Credential for login",
    "version": "0.7",
    "@type": "https://w3c-ccg.github.io/vc-json-schemas/schema/1.0/schema.json",
    "schema": {
        "$schema": "http://json-schema.org/draft-07/schema#",
        "type": "object",
        "required": ["name","customer", "customerId", "organization", "organizationId", "type", "configId", "configName"],
        "properties": {
            "customer": {
                "type": "string",
                "visible": true
            },
            "customerId": {
                "type": "string",
                "visible": false
            },
            "name": {
                "type": "string",
                "visible": true
            },
            "organization": {
                "type": "string",
                "visible": true
            },
            "organizationId": {
                "type": "string",
                "visible": false
            },
            "configName": {
                "type": "string",
                "visible": true
            },
            "configId": {
                "type": "string",
                "visible": false
            },
            "type": {
                "type": "string",
                "visible": false
            },
            "verifierType": {
                "description": "Verifier types defined by customer.",
                "type": "string",
                "visible": true
            }
        }
    }

}
